import React, { useState } from "react";
import Card from "../UI/Card";
import classes from './AddUsers.module.css'
import Button from "../UI/Button";

const AddUsers = (props) => {

  // Js syntax which is Array destructuring 
  // useState return exactly 2 elements thats why we use this syntax
  const [enteredUsername, setEnteredUsername] = useState('');
  const [enteredAge, setEnteredAge] = useState('');

  const addUserHandler = (event) => {
    event.preventDefault();
    console.log(enteredUsername, enteredAge);
    setEnteredUsername('');
    setEnteredAge('');
  };

  const usernameChangeHandler = (event) => {
    setEnteredUsername(event.target.value);

  }
  const ageChangeHandler = (event) => {
    setEnteredAge(event.target.value);

  }
  return (
    <Card className={classes.input}>
    <form onSubmit={addUserHandler}>
      <label htmlFor="username" value={se} onChange={usernameChangeHandler}>Username</label>
      <input id="username" type="text" />
      <label htmlFor="age" onChange={ageChangeHandler}>Age (years)</label>
      <input id="age" type="number" />
      <Button type="submit">Add User</Button>
    </form>
    </Card>
  );
};

export default AddUsers;
